def calculate_fibonacci(n):
    """Calculate the nth Fibonacci number"""
    # Convert string to int if needed
    if isinstance(n, str):
        n = int(n)

    if n <= 0:
        return 0
    elif n == 1:
        return 1
    else:
        a, b = 0, 1
        for _ in range(2, n + 1):
            a, b = b, a + b
        return b
